document.addEventListener('DOMContentLoaded',()=>{const b=document.querySelector('.menu-toggle');const n=document.querySelector('nav');if(b&&n){b.addEventListener('click',()=>{const o=n.classList.toggle('open');b.setAttribute('aria-expanded',o?'true':'false');});}document.querySelectorAll('img[data-src]').forEach(i=>{i.src=i.dataset.src;i.removeAttribute('data-src');});});
